changeSet.sql:
create table country ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(100), english_name varchar(100) );
create table state ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(100), english_name varchar(100), country_id bigint, foreign key (country_id) references country (id) );
create table city ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(100), english_name varchar(100), state_id bigint, foreign key (state_id) references state (id) on delete cascade );
create table user_ ( id bigint auto_increment primary key, active boolean default 1, first_name varchar(255), last_name varchar(255), phone_number varchar(255), email varchar(255), password varchar(255), uid varchar(100), username varchar(100) );
create table user_authorities ( user_id bigint, authority varchar(100), primary key (user_id, authority), foreign key (user_id) references user_ (id) on delete cascade );
create table donation ( id bigint auto_increment primary key, active boolean default 1, donation_date datetime, expiration_date datetime, title varchar(250), description varchar(500), category varchar(100), communication_method varchar(100), state varchar(100), city_id bigint, user_id bigint, foreign key (city_id) references city (id) on delete cascade, foreign key (user_id) references user_ (id) on delete cascade );
create table medicine_unit ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(100), english_name varchar(100) );
create table medicine_category ( id bigint primary key auto_increment, active boolean, arabic_name varchar(100), english_name varchar(100) );
create table medicine ( id bigint primary key, active boolean default 1, arabic_name varchar(255), english_name varchar(255), category_id bigint, unit_id bigint, info varchar(100), foreign key (category_id) references medicine_category (id) on delete cascade, foreign key (unit_id) references medicine_unit (id) on delete cascade );
create table medicine_donation ( id bigint primary key auto_increment, active boolean default 1, medicine_expiration_date datetime, amount double precision, unit_id bigint, donation_id bigint, medicine_id bigint, foreign key (medicine_id) references medicine (id) on delete cascade, foreign key (donation_id) references donation (id) on delete cascade, foreign key (unit_id) references medicine_unit (id) on delete cascade );
